import { Component } from '@angular/core';
interface Post {
  userId: number,
  id: number,
  title: string,
  body: string
};
@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent {

  public posts:Post[] = [];
  title = "Home";
  public width:number = 300;
  public height:number = 300;
  public imageSource:string = "https://png.pngtree.com/element_our/sm/20180516/sm_5afc6f6f0d492.jpg";

  ngOnInit(){

    fetch('https://jsonplaceholder.typicode.com/posts') // go to this url and get the data
      .then(response => response.json()) // convert the response to json thru a promise
      .then( (blogPosts) => { // take the json and do something with it
        this.posts = blogPosts;
        // console.log(this.posts)
      });
  }


  addPost(){

    let postNumber = this.posts.length + 1;
    let toFixed:any = Math.random();
    let postBodyNo: any = toFixed.toFixed(3) * 1000;
    let newPost:Post = {
      userId: 1,
      id: 9999,
      title: 'New Post ' + postNumber,
      body: 'New Post Body ' + postBodyNo
    };

    this.posts.unshift(newPost);
  }


}
<div class="row">
    <div class="col-md-12">
        <h2 class="text-center my-5">{{title | uppercase}}</h2>
    </div>


    <div class="col-md-10 mx-auto">
        <img [src]='imageSource' [width]="width" [height]="height" class="img-fluid d-block mx-auto">
    </div>  

    <div class="col-md-12">
        <button class="btn btn-primary mx-auto d-block my-2" (click)="addPost()">Add Post</button>
    </div>

    <div class="col-md-12" *ngIf="posts.length > 0">
        <h2 class="text-center my-5">Posts</h2>

        <div class="row" *ngFor="let post of posts">
            <div class="col-md-6 mx-auto my-5">
                <h3>{{post.title}}</h3>
                <p>{{post.body}}</p>

            </div>
        </div>

    </div>
</div>
